<template>
  <div>
    <transition name="translate">
      <div class="map_filters" v-if="sideBar">
        <div class="title-brand"><span class="brand">Moove Map</span></div>
        <div class="input-wrapper">
          <input
            type="search"
            class="search"
            v-model="search"
            placeholder=" Buscar"
          />
          <svg
            xmlns="http://www.w3.org/2000/svg"
            class="input-icon"
            viewBox="0 0 20 20"
            fill="currentColor"
          >
            <path
              fill-rule="evenodd"
              d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z"
              clip-rule="evenodd"
            />
          </svg>
        </div>
        <div class="menu-list">
          <ul>
            <li
              v-for="item in menu"
              :key="item.name"
              @click="item.isOpen = !item.isOpen"
            >
              <hr v-if="menu_filter(item)"
                style="
                  opacity: 0.1;
                  background-color: black;
                  width: 90%;
                  margin-top: 30px;
                  margin-bottom: 0px;
                "
              />
              <div v-show="menu_filter(item)" class="menu1">
                {{ item.name }}
              </div>
              <ul class="menu2" v-show="item.isOpen">
                <li v-for="child in item.children" :key="child.name">
                  <div v-if="!child.selected"
                    v-show="childFilter(child, item) && item.isOpen"
                    @click.stop.prevent="
                      $emit('launch-query', child.tags), (sideBar = false), toggleSelected(child, item.children)
                    "
                    :class="['map_filter', child.icon]"
                  >
                    {{ child.name }}
                  </div>
                  <div v-else class="item_selected"
                    v-show="childFilter(child, item) && item.isOpen"
                    @click.stop.prevent="toggleSelected(child)"
                    :class="['map_filter', child.icon]"
                  >
                    <span>{{ child.name  + "   "}}</span>
                    <span class="results">{{ " " + $parent.results_count }}</span>
                  </div>
                </li>
              </ul>
            </li>
          </ul>
        </div>
      </div>
    </transition>
    <button v-if="sideBar" @click="openCloseSideBar">
      <img
        class="arrows"
        src="../../public/res/arrows.png"
        style="width: 20px; height: 20px"
        alt=""
      />
    </button>
    <button v-else class="toOpen" @click="openCloseSideBar">
      <img
        class="arrows"
        src="../../public/res/arrows.png"
        style="width: 20px; height: 20px"
        alt=""
      />
    </button>
  </div>
</template>

<script>
import menu_data from "../menu-data.json";

export default {
  name: "nodes-filter",
  props: ["filter"],
  data: function () {
    return {
      menu: menu_data,
      sideBar: true,
      button: true,
      search: "",
      toggle_selected: ""
    };
  },
  methods: {
    toggleSelected: function (child, children) {
      children.forEach(element => {
        element.selected = false;
      });
      child.selected = true;
    },
    menu_filter: function (item) {
      var valThis = this.search.toLowerCase();
      if (!valThis || valThis == "") {

        return true;
      }
      if(item.name.toLowerCase().includes(valThis)){
        return true;
      }
       return false;
      
    },
    childFilter(child,item) {
      var valThis = this.search.toLowerCase();
      if (valThis != "") {
        if (child.name.toLowerCase().includes(valThis)) {
          item.isOpen = true;
          return true;
        }
      } else if (valThis == "") {
        return true;
      }
      return false;
    },
    openCloseSideBar: function () {
      this.sideBar = !this.sideBar;
    },
  },
};
</script>

